import { useEffect, useState } from "react";
import { addDoc, 
         collection, 
         serverTimestamp, 
         onSnapshot, 
         query,
         where,
         orderBy} from 'firebase/firestore';
import { auth, db } from "../firebase-config";
import './styles/chat.css'

export const Chat = (props) => {
    const {room} = props;
  const [newMessage, setNewMessage] = useState("");
const [messages, setMessages] = useState([])
  const messagesRef = collection(db, "messages");


   useEffect(() => {
      const queryMessages = query(
      messagesRef, 
      where("room","==",room), 
      orderBy("createdAt")
    );
      const unsubscribe =  onSnapshot(queryMessages, (snapshot) => {
        let messages = [];
              snapshot.forEach((doc) => {
              messages.push({...doc.data(), id: doc.id});
              });
        setMessages(messages);
      });
      return () => unsubscribe();
   }, []);

  const handleSubmit = async (e) =>{
  e.preventDefault();
  if (newMessage === "") return;

  await addDoc(messagesRef, {
    text: newMessage,
    createdAt: serverTimestamp(),
    utilisateur: auth.currentUser ? auth.currentUser.displayName : "Utilisateur inconnu",
    photo: auth.currentUser.photoURL,
    room,
  });

  setNewMessage("");
};

    return <div className="chat-app">
        <div className="header">
        <h1>Bienvenue dans le salon {room.toUpperCase()}</h1>
        </div>
    <div className="messages"> 
        {messages.map((message) => 

        <div className="message" key={message.id}>
        <img className="photo-utilisateur" src={message.photo}></img>
        <div className="nom-utilisateur">{message.utilisateur}</div>
        <div>{message.text}</div>
        </div>


        )}
    </div>
    <form onSubmit={handleSubmit} className="new-message-form">
        <input 
        className="new-message-input" 
        placeholder="Ã‰crire un message ici..."
        onChange={(e) => setNewMessage(e.target.value)}
        value={newMessage}
        />
            <button type="submit" className="send-button">
                Envoyer
            </button>
            </form> </div>
};
